mapper.select:
select * from wish 
where 
	wi_me_num = #{wi_me_num} and wi_sb_num = #{wi_sb_num}

mapper.delete:
delete from wish 
where 
	wi_me_num = #{wish.wi_me_num} and wi_sb_num = #{wish.wi_sb_num}

mapper.update:
update sale_board
set
	sb_wish = sb_wish + #{i}
where
	sb_num = #{wi_sb_num}

mapper.select:
select * from sale_board where sb_ss_num = 1
and sb_sc_num = #{cri.sc_num}
order by sb_date desc limit #{cri.pageStart}, #{cri.perPageNum}

select * from sale_board order by sb_num desc
limit #{cri.pageStart},
#{cri.perPageNum}

select * from sale_image 
where 
	si_table = #{si_table} and si_key = #{sb_num}
